    if(typeof data !== 'string') {
      data = Buffer.from(data.toString());
    }
    else {
      data = Buffer.from(data || '', options.encoding || 'utf8');
    }
  }

  open_file(context, path, flags, function(err, fileNode) {
    if(err) {
