import * as THREE from "three";

export class MMOD {
  public readonly mesh: THREE.Mesh;

  /**
   * A small point object representing orbiting debris.
   */
  constructor(earthRadius: number) {
    // The radius of the earth in real life is 6,371 km.
    // Low earth orbit is anywhere 2,000 km from the surface.
    // We want to extend the given earth radius by a ratio of

    const geometry = new THREE.SphereGeometry(1, 4, 2);
    const material = new THREE.MeshPhongMaterial({
      color: new THREE.Color().setHSL(
        Math.random(),
        0.5,
        0.5,
        THREE.SRGBColorSpace
      ),
      side: THREE.DoubleSide,
      alphaToCoverage: true,
    });
    this.mesh = new THREE.Mesh(geometry, material);

    // Points on the surface of a sphere can be expressed using two
    // spherical coordinates, theta and phi with 0 < theta < 2pi and
    // 0 < phi < pi.
    // Generate random values for theta and phi.
    const theta = this.random(0, 2 * Math.PI);
    const phi = this.random(0, Math.PI);
    // Convert theta and phi into cartesian coordinates for the mesh.
    this.mesh.position.x = this.mesh.position.y = Math.random() * 10 - 5;
    this.mesh.position.z = Math.random() * 10 - 5;
    this.mesh.scale.x =
      this.mesh.scale.y =
      this.mesh.scale.z =
        Math.random() * 3 + 1;
  }

  private random(min: number, max: number): number {
    // min and max included
    return Math.floor(Math.random() * (max - min + 1) + min);
  }
}
